import './AddCategoryForm.css';
import {useRef} from 'react'

function AddCategoryForm(props) {

    const nameRef = useRef()
    const typeRef = useRef()

    function formSubmitHandler(e) {
        e.preventDefault();

        const category = {
            name: nameRef.current.value,
            category: typeRef.current.value
        }

        props.onAddCategory(category)
    }

    return (
      <form onSubmit={formSubmitHandler}>
      <label><b>Kategooria nimi</b></label> <br/>
      <input type="text" placeholder="Nimi" required ref={nameRef}/><br/>
      <label htmlFor="type"><b>Kategooria tüüp</b></label><br/>
      <select id="type" required ref={typeRef}>
      <option value="BASIC" default><b>Basic</b></option>
      <option value="DELUXE"><b>Deluxe</b></option>
      <option value="PREMIUM"><b>Premium</b></option>
      </select><br/><br />
      <button><b>Lisa Kategooria</b></button>
  </form>
    )
}

export default AddCategoryForm
