import { Link } from "react-router-dom";

function Item(props) {
  function handleDelete(itemId){
    props.deleteItem(itemId);
  }

  return (
    <div>
      {props.isSingleItemVie}
      <Link to={`item/${props.id}`}>
      <div className="itemName"><b>Name:</b> {props.name}</div>
      <div className="itemPrice"><b>Price:</b> {props.price}</div>
      <div className="itemCategory"><b>Category:</b> {props.category}</div>
      </Link>
      { props.isAddToCartButton ? <button>Lisa ostukorvi</button> : 
      <div>
        <button onClick={()=>handleDelete(props.id)}>X</button>
        <button>Muuda toodet</button>
      </div>}
      <br />
    </div>
  )
}

export default Item;
